def read_dataframe(file_path, file_type, header=None, schema=None, verbose=False):    
    if file_type is None: file_type = file_path.split('.')[-1]
    df = spark.read.format(file_type)
    if header is not None: df = df.option('header', header)
    if schema is not None: df = df.schema(schema)
    df = df.load(file_path)

    if verbose:
        df.printSchema()
        df.show()

    return df

def write_dataframe(df, path, partitions, csv=True, parquet=False, json=False):
    df = df.repartition(partitions)

    if csv: df.write.csv(f"{filepath}csv/", mode='overwrite', header = 'true') 
    if parquet: df.write.parquet(f"{filepath}parquet/", mode='overwrite') 
    if json: df.write.json(f"{filepath}json/", mode='overwrite')
