def read_dataframe(path, file_type=None, header=None, schema=None, verbose=False):    
    if file_type is None: file_type = path.split('.')[-1]
    df = spark.read.format(file_type)
    if header is not None: df = df.option('header', header)
    if schema is not None: df = df.schema(schema)
    df = df.load(path)

    if verbose:
        df.printSchema()
        df.show()

    return df

def write_dataframe(df, path, partitions, csv=True, parquet=False, json=False):
    df = df.repartition(partitions)

    if csv: df.write.csv(f"{path}csv/", mode='overwrite', header = 'true') 
    if parquet: df.write.parquet(f"{path}parquet/", mode='overwrite') 
    if json: df.write.json(f"{path}json/", mode='overwrite')

def extract_datetime_attributes(dt: str):
    # Convert to datetime object
    dt = datetime.strptime(dt, "%Y-%m-%d %H:%M:%S")

    # Extract year, month and day from pipeline trigger time
    return (dt.year, dt.month, dt.day, dt.hour, dt.minute, dt.second)
